package com.marcelherd.uebung3.sightseeing;

import com.marcelherd.uebung3.sightseeing.exceptions.FatalFlightSimulatorException;
import com.marcelherd.uebung3.sightseeing.exceptions.GeneralFlightSimulatorException;
import com.marcelherd.uebung3.sightseeing.exceptions.PlaneTooHeighException;
import com.marcelherd.uebung3.sightseeing.exceptions.PlaneTooLowException;
import com.marcelherd.uebung3.sightseeing.exceptions.SimulatorConfigurationException;
/**
 * Plane implementation for Flight Simulator
 * 
 * @author Manuel Schwalm
 * @author Marcel Herd
 */
public class MyPlane implements Plane{

	private boolean atAirport = true;
	private boolean doorsClosed = false;
	private FlightRoute route;
	private int positionInKilometers = 0;
	private int height = 0;
	
	public MyPlane(FlightRoute route){
		this.route = route;
	}
	
	@Override
	public void openDoors() throws GeneralFlightSimulatorException {
		if(!atAirport)
			throw new GeneralFlightSimulatorException("Doors can't be opened while in Air");
		
		doorsClosed = false;
	}

	@Override
	public void closeDoors() {
		doorsClosed = true;
	}

	@Override
	public void stop() throws GeneralFlightSimulatorException {
		if(!atAirport)
			throw new GeneralFlightSimulatorException("Plane can't be stopped while the plane is in the air");
		else if(height > 0){
			throw new FatalFlightSimulatorException("Fatal Error, plane is still in air. Stop Simulation...");
		}
		openDoors();
	}
	
	@Override
	public void flyNextKilometer(int additionalHeight) throws GeneralFlightSimulatorException {
		if(!doorsClosed){
			throw new GeneralFlightSimulatorException("The plane is only able to start, if doors are closed!");
		}
		
		if(positionInKilometers == route.getLength()){
			throw new GeneralFlightSimulatorException("The plane is already at the target, it have to stop!!");
		}
		
		
		if(height + additionalHeight < 0)
			throw new PlaneTooLowException("The plane can't fly into a negative height: " + (height + additionalHeight));
		
		if(additionalHeight > 100)
			throw new GeneralFlightSimulatorException("The plane would change the height " + (additionalHeight - 100) + "m to much between the next km. Current height: " + height);
		if(additionalHeight < -100)
			throw new GeneralFlightSimulatorException("The plane would change the height " + (additionalHeight + 100) + "m to much between the next km. Current height: " + height);
		
		height += additionalHeight;
		atAirport = false;
		positionInKilometers++;
		
		// Currently above the city
		if(positionInKilometers > 2 && positionInKilometers < route.getLength() - 2){
			if(height < route.getMinHeight())
				throw new PlaneTooLowException("The plane flies " + (route.getMinHeight() - height) + "m to deep. Current height: " + height + "m");
			if(height > route.getMaxHeight())
				throw new PlaneTooHeighException("The plane flies " + (height - route.getMaxHeight()) + "m to high. Current height: " + height + "m");
		}else if(positionInKilometers == route.getLength()){
			atAirport = true;
			if(height > 0){
				throw new FatalFlightSimulatorException("Fatal Error, plane is at the target and still in air. Stop Simulation...");
			}
		}
	}
	
	@Override
	public String toString(){
		String retval = "Height: " + height + "m length: " + positionInKilometers + "km\nkm until airport: " + (route.getLength() - positionInKilometers);
		if(positionInKilometers <= 2 || route.getLength() - positionInKilometers <= 2){
			retval += "\ncurrently above the city: no";
		}else{
			retval += "\ncurrently above the city: yes";
		}
		return retval;
	}

}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 